import React, { Fragment, Component } from 'react';
import { fib } from '../utils/helper';

class Fibonacci extends Component {
    constructor(props) {
        super(props)

        this.state = {
            input: 0,
        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({input: Number(e)})
    }

    render() {
        let { input } = this.state;
        console.log(fib(input-1));
        // input = Number(input-2);
        // const arr = fib(input);
        // let result;
        // if (arr.length > 0) {
        //     result = (arr.join(', '))
        // }
        // console.log(result);
        return (
            <Fragment>
                <div className="disflex">
                    <label>Input:</label>
                </div>
                <div className="disflex">
                    <input
                        type="number"
                        onChange={e => this.handleChange(e.target.value)}
                    />
                </div>
                <div className="disflex">
                    <label style={{ marginTop: '40px' }}>Result</label>
                </div>
                <div className="disflex">
                    <input className="w-100" disabled />
                </div>
            </Fragment>
        );
    }
}
export default Fibonacci;
